mapper.select:
select e.id as evidence_id, e.customer_id as evidence_customer_id, e.type as evidence_type
from source_evidences e
where e.customer_id = #{customer_id} limit #{from}
    , #{size}

select e.id as evidence_id, e.customer_id as evidence_customer_id, e.type as evidence_type
from source_evidences e
where e.id = #{id}
  and e.customer_id = #{customer_id}

mapper.insert:
insert into transactions (account_id, source_evidence_id, amount, currency, created_at)
values (#{account_id}, #{evidence_id},
        #{description.amount.value}, #{description.amount.currency}, #{description.createdAt});

mapper.update:
update accounts
set current_amount = current_amount + #{change.total.value}
where customer_id = #{customer_id}
  and id = #{id};

mapper.select:
select count(id)
from transactions
where account_id = #{id}

select count(id)
from source_evidences
where customer_id = #{id}

select count(id)
from transactions
where source_evidence_id = #{id}
